// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.7.0 <0.9.0;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

contract Lottery is Ownable {
    ///@notice Flag indicating if lotthery is open for bets
    bool public betsOpen;
    /// @notice Timestamp of the lotter cnext closing date
    uint256 public betsClosingTime;

    constructor() {}

    /// @notice Passes when the lottery is at closing state
    modifier whenBetsClosed() {
        require(!betsOpen, "Lottery: Lottery is open");
        _;
    }

    /// @notice opens the lotter for receiving bets
    function openBets(uint256 closingTime) external onlyOwner whenBetsClosed {
        require(
            closingTime > block.timestamp,
            "Lottery: Closing time must be in the future"
        );
        betsClosingTime = closingTime;
        betsOpen = true;
    }
}

